const txtImage = `data:image/jpeg;base64,`;
const PdfDoc = require('pdfkit');

function toCurrency(number) {
    const string = parseFloat(number).toFixed(2);
    const integer = string.split('.')[0].split('').reverse().reduce((array, item, index) => {
        const number = Math.floor(index / 3);
        array[number] = array[number] || [];
        array[number].push(item);
        return array;
    }, []).map(a => a.reverse()).reverse().join('.').replace(/,/g, '');
    const decimals = string.split('.')[1];
    return `Euro ${integer},${decimals}`;
}

function addItem(doc, params) {
    const { CLIENTE, NUMERO, FECHA, DESCRIPCIONES, TOTAL, IVA } = params;
    const marginLeft = 30;
    let posY;

    doc.image(txtImage, 15, 15, { width: 180, height: 100 });
    doc.rect(290, 15, 300, 150).stroke();

    doc
        .fontSize(20)
        .font('Helvetica-Bold')
        .fillColor('black')
        .text('FACTURA', 310, 25);

    doc
        .moveTo(310, 50)
        .lineTo(570, 50)
        .stroke();

    doc
        .fontSize(10)
        .font('Helvetica')
        .text('Cliente:', 310, 70);

    doc
        .fontSize(11)
        .font('Helvetica')
        .text(CLIENTE, 310, 85);

    posY = 200;
    doc
        .fontSize(12)
        .text(`Numero: ${NUMERO}`, marginLeft, posY);

    doc
        .fontSize(12)
        .text(`Fecha: ${FECHA}`, marginLeft + 250, posY);

    doc
        .moveTo(marginLeft, 215)
        .lineTo(400, 215)
        .stroke();

    posY += 50;
    doc
        .fontSize(9)
        .text('Codigo', marginLeft, posY)
        .text('Descripcion', marginLeft + 50, posY)
        .text('Base Imponible', marginLeft + 300, posY)
        .text('% IVA', marginLeft + 400, posY)
        .text('Importe', marginLeft + 450, posY);

    posY += 30;

    DESCRIPCIONES.forEach(function (inner, index) {
        doc
            .fontSize(11)
            .text(`${index + 1}`, marginLeft, posY)
            .text(inner.DESCRIPCION, marginLeft + 50, posY, { width: 200, height: 30 })
            .text(toCurrency(inner['BASE IMPONIBLE']), marginLeft + 300, posY)
            .text(inner['PORCENTAJE IVA'], marginLeft + 400, posY)
            .text(toCurrency(inner.TOTAL), marginLeft + 450, posY);
        posY += 30;
    });

    posY = 600;
    doc
        .moveTo(marginLeft, posY)
        .lineTo(570, posY)
        .stroke();

    posY += 10;
    doc
        .text('HIDIME BELLEZA S.L.', marginLeft, posY + 15)
        .text('Calle Salitre 11, 29002 Malaga', marginLeft, posY + 30)
        .text('NIF: B93140986', marginLeft, posY + 45);

    doc
        .text('BASE IMPONIBLE', marginLeft + 250, posY + 20)
        .text(toCurrency(params['BASE IMPONIBLE']), marginLeft + 400, posY + 20, { width: 90, align: 'right' });

    doc
        .text('%', marginLeft + 250, posY + 40)
        .text(params['PORCENTAJE IVA'], marginLeft + 400, posY + 40, { width: 90, align: 'right' });

    doc
        .text('IVA', marginLeft + 250, posY + 60)
        .text(toCurrency(IVA), marginLeft + 400, posY + 60, { width: 90, align: 'right' });

    doc
        .fontSize(14)
        .text('TOTAL', marginLeft + 250, posY + 80)
        .text(toCurrency(TOTAL), marginLeft + 400, posY + 80, { width: 90, align: 'right' });

}

module.exports = function (res, items) {
    const doc = new PdfDoc();
    doc.pipe(res);

    items.forEach(function (item, index, array) {
        addItem(doc, item);
        if (index < array.length - 1) {
            doc.addPage();
        }
    });

    doc.end();
};
